import React, { useState, useEffect } from 'react';
import api from '../utils/Api';

function Main(props) {
  const [userName, setUserName] = useState();
  const [userDescription, setUserDescription] = useState();
  const [userAvatar, setUserAvatar] = useState();
  const [cards, setCards] = useState([]);

  useEffect(() => {
    api
      .getMyInfo()
      .then((res) => {
        setUserName(res.name);
        setUserDescription(res.about);
        setUserAvatar(res.avatar);

        return api.Cards();
      })
      .then((res) => {
        setCards(res);
      });
  }, []);


  return (
    <main className="main">
    <section className="profile">
    <button className="profile__avatar-edit" onClick={props.onEditAvatar}>
      <img className="profile__avatar" style={{ backgroundImage: `url(${userAvatar})` }} alt='аватар'/>
    </button>
    <div className="profile__info">
      <div className="profile__name-container">
       <h1 className="profile__name">{userName}</h1>
       <button className="profile__edit-button button" type="button" onClick={props.onEditProfile}></button>
      </div>
      <p className="profile__about">{userDescription}</p>
    </div>
    <button className="profile__add-button button" type="button" onClick={props.onAddPlace}></button>
    </section>
    <section className="places">
    {cards.map((card) => (
      <div className="place" key={card._id}>
        <img className="place__photo" src={card.link} alt='изображение'/>
        <div className="place__description">
          <h3 className='place__heading'>{card.name}</h3>
          <div className ='place__like-container'>
            <button className='place__like button' type="button"></button>
            <span className='place__like-counter'>{card.likes.length}</span>
          </div>
        </div>
      </div>
    ))}
    </section>
  </main>
  )
}

export default Main
